settings.custom_liquid:
{% if product.handle == 'bottle-bumper-guard-large' or product.handle == 'bottle-bumper-guard-small' %}
  <div class="custom3-product-switcher-wrapper">
    <div class="custom3-title">Choose Bumper Size</div>
    <div class="custom3-product-switcher">
      <a href="/products/bottle-bumper-guard-small" class="custom3-switcher-button {% if product.handle == 'bottle-bumper-guard-small' %}custom3-active{% endif %}">
        Small | 20 oz
      </a>
      <a href="/products/bottle-bumper-guard-large" class="custom3-switcher-button {% if product.handle == 'bottle-bumper-guard-large' %}custom3-active{% endif %}">
        Big | 34 oz
      </a>
    </div>
  </div>
{% endif %}

<style>
.custom3-product-switcher-wrapper {
  margin-bottom: 20px;
}

.custom3-title {
  font-size: 1.3rem; 
  margin-bottom: 5px;
  color: #000000;
}

.custom3-product-switcher {
  display: flex;
  gap: 10px;
  justify-content: flex-start; /* Aligns buttons to the left */
  flex-wrap: wrap; /* Ensures buttons wrap if needed on smaller screens */
}

.custom3-switcher-button {
  padding: 8px 16px; /* Adjusted size */
  border-radius: 5px;
  background-color: #e8e8e8; /* Slightly lighter background */
  border: 1px solid #000; /* Thin black border */
  color: #333;
  text-decoration: none;
  font-weight: 400; /* Text weight */
  font-size: 1rem; /* Default font size */
  transition: background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease;
  white-space: nowrap; /* Prevents text from wrapping inside buttons */
  min-width: 100px; /* Set a minimum width to match the widest button */
  display: inline-flex;
  justify-content: center; /* Center content inside the button */
  margin-bottom: 10px;
}

.custom3-switcher-button:hover {
  background-color: #dcdcdc; /* Slightly darker on hover */
}

.custom3-switcher-button.custom3-active {
  background-color: #1f1f1f; /* Blackish background for active button */
  color: #fff; /* White text for active button */
  border-color: #555; /* Slightly darker border for active button */
}

/* Mobile Styles */
@media (max-width: 768px) {
  .custom3-switcher-button {
    font-size: 1rem; /* Font size on mobile */
    padding: 6px 12px; /* Smaller padding for mobile */
    min-width: 120px; /* Adjust width for mobile to fit smaller screens */
  }

  .custom3-product-switcher {
    gap: 5px; /* Reduce gap between buttons on mobile */
  }
}
</style>
